var asynth = require('../');
var s = asynth(function (note, t) {
    var freq = 440 * Math.pow(2, (note.key - 49) / 12);
    var x = Math.sin(2 * Math.PI * t * freq);
    var y = Math.sin(2 * Math.PI * t * (freq * 5));
    var z = Math.sin(2 * Math.PI * t * (freq / 2));
    return x * 1 + y * 0.5 + z * 0.5;
});

s.play();
